/* button_showmore */

.button_showmore {
  border-radius: 20px;
  background-color: #ff3366;
  border: none;
  color: #FFFFFF;
  text-align: center;
  font-size: 15px;
  padding: 10px;
  width: 250px;
  transition: all 0.5s;
  cursor: pointer;
  margin: 5px;
  font-weight:700;
  font-family: Open Sans,sans-serif;
  letter-spacing: -0.5px;
}

.button_showmore span {
  cursor: pointer;
  display: inline-block;
  position: relative;
  transition: 0.5s;
}

.button_showmore span:after {
  content: '\00bb';
  position: absolute;
  opacity: 0;
  top: 0;
  right: -20px;
  transition: 0.5s;
}

.button_showmore:hover span {
  padding-right: 25px;
}

.button_showmore:hover span:after {
  opacity: 1;
  right: 0;
}
